(function($){$.fn.ztinputhint=function(g){var h={hint:null,mode:'field',sourceAttrib:'title',hintClass:false,defaultClass:false,hintBG:false,defaultBG:false};if(g&&typeof g=='object'){$.extend(h,g)}else if(g){h.hint=g}return this.each(function(){var a=$(this);if(!a.is('input:text, input:password')){return false}if(h.mode=='field'){var b='ztInputHint_'+a.attr('name');var c='<input type="text" value="" style="display: none;" />';var d=h.hint||a.attr(h.sourceAttrib);var e=h.hintClass||a.attr('class');$(c).insertBefore(a);var f=a.prev('input:first');if(d){f.attr('class',e);f.attr('size',a.attr('size'));f.attr('tabIndex',a.attr('tabIndex'));f.val(d);f.focus(function(){$(this).hide();cont=$(this).next('input:first');cont.show();cont.focus()});a.blur(function(){if($(this).val()==''){$(this).prev('input:first').show();$(this).hide()}});if(h.hintClass&&!a.hasClass(h.hintClass)){a.addClass(h.hintClass)}a.blur()}}else if(h.mode=='class'){a.blur(function(){if($(this).val()==''){if(h.defaultClass!=false&&a.hasClass(h.defaultClass))a.removeClass(h.defaultClass);if(h.hintClass!=false&&!a.hasClass(h.hintClass))a.addClass(h.hintClass)}});a.focus(function(){if(h.hintClass!=false&&a.hasClass(h.hintClass))a.removeClass(h.hintClass);if(h.defaultClass!=false&&!a.hasClass(h.defaultClass))a.addClass(h.defaultClass)});a.blur()}else if(h.mode=='background'){a.css('background-position','center left');a.css('background-repeat','no-repeat');a.css('background-color','transparent');a.css('background-attachment','scroll');a.blur(function(){if($(this).val()==''&&h.hintBG!='')a.css('background-image','url('+h.hintBG+')')});a.focus(function(){a.css('background-image','');if(h.defaultBG!=''&&a.css('background-image')!='url('+h.defaultBG+')'){a.css('background-image','url('+h.defaultBG+')')}});a.blur()}})};$.fn.ztInputHint=$.fn.ztinputhint})(jQuery);